//刪除同樣的名字
var menus = []
function getMenus() {
	for (var i = 0; i < fooddata.length; i++) {
		var isNew = true;
		for (var m = 0; m < fooddata.length; m++) {
			if (menus[m] == fooddata[i].district) {
				isNew = false;
			}
		}
		if (isNew) {
			menus.push(fooddata[i].district)
		}
	}
}
getMenus()



//把資料放進去select
var addOpstion = '';
for (var s = 0; s < menus.length; s++) {
	var menu = menus[s];
	addOpstion += '<option value="' + menu + '">' + menu + '</option>'
}
area.innerHTML = '<option>--請選擇行政區--</option>' + addOpstion;

//當滑鼠點擊或更換時促發
function updateList(e) {
	var selector = e.target.value;
	var boxStr = '';
	for (var i = 0; i < fooddata.length; i++) {
		if (selector == fooddata[i].district) {
            title.textContent = fooddata[i].district;
			boxStr += '<div class="box"><div class="box-top">';
			boxStr += '<h3>' + fooddata[i].name + '</h3>';
			boxStr += '<h5>' + fooddata[i].district + '</h5></div>';
			boxStr += '<div class="box-bottom"><p><i class=" text-warning mr-1 fas fa-clock"></i>' + fooddata[i].open_time + '</p>';
			boxStr += '<p><i class="fas fa-location-arrow text-warning mr-1"></i>' + fooddata[i].address + '</p>';
			boxStr += '<p><i class="fas fa-phone text-warning mr-1"></i>' + fooddata[i].tel + '</p>';
			boxStr += '<p class="catgory">' + fooddata[i].category + '<i class="fas fa-tag text-warning ml-1"></i></p></div></div>';
		}
	}
	container.innerHTML = boxStr;
}

//畫面進來時自動出現的
