var title = document.querySelector('.jstitle');
var container = document.querySelector('.jsmain');
var btns = document.querySelectorAll('.jsbtn');

//全部button的按鈕
for (var i = 0; i < btns.length; i++) {
	btns[i].addEventListener('click', updateList, false);
}



//刪除同樣的名字
var menus = []
function getMenus() {
	for (var i = 0; i < hoteldata.length; i++) {
		var isNew = true;
		for (var m = 0; m < hoteldata.length; m++) {
			if (menus[m] == hoteldata[i].category) {
				isNew = false;
			}
		}
		if (isNew) {
			menus.push(hoteldata[i].category)
		}
	}
}
getMenus()

//當滑鼠點擊或更換時促發
function updateList(e) {
	var selector = e.target.value;
	var boxStr = '';
	for (var i = 0; i < hoteldata.length; i++) {
		if (selector == hoteldata[i].category) {
            title.textContent = hoteldata[i].category;
			boxStr += '<div class="card view-card"><div class="card-header">';
			boxStr += '<h3>' + hoteldata[i].name + '</h3>';
			boxStr += '<h5>' + hoteldata[i].category + '</h5></div>';
			boxStr += '<div class="card-body"><p><i class="fas fa-location-arrow text-primary mr-1"></i>' + hoteldata[i].address + '</p>';
			boxStr += '<p><i class="fas fa-phone text-primary mr-1"></i>' + hoteldata[i].tel + '</p>';
			boxStr += '<p><i class="fas fa-print text-primary mr-1"></i>' + hoteldata[i].fax + '</p></div></div>';
		}
	}
	container.innerHTML = boxStr;
}

//畫面進來時自動出現的
var str = '';
for (var i = 0; i < hoteldata.length; i++) {
	if (hoteldata[i].category == '旅館') {
		title.textContent = hoteldata[i].category;
		str += '<div class="card view-card"><div class="card-header">';
		str += '<h3>' + hoteldata[i].name + '</h3>';
		str += '<h5>' + hoteldata[i].category + '</h5></div>';
		str += '<div class="card-body"><p><i class="fas fa-location-arrow text-primary mr-1"></i>' + hoteldata[i].address + '</p>';
		str += '<p><i class="fas fa-phone text-primary mr-1"></i>' + hoteldata[i].tel + '</p>';
		str += '<p><i class="fas fa-print text-primary mr-1"></i>' + hoteldata[i].fax + '</p></div></div>';
	}
}
container.innerHTML = str;
